
public class program5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int year = 2021 ;
		if (year % 4 == 0) {
			if(year % 100 == 0) {
				if(year % 400 == 0) {
	    
			           System.out.println("is a leap year");
			           }
			       else {
			        System.out.println("is not a leap year");
			       }
			       }
		}
	}
}
	